INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('7', '4', 'ge', 'ნინო წამალაძე', '<p>საბეჭდი და ტიპოგრაფიული ინდუსტრიის უშინაარსო ტექსტია. იგი სტანდარტად 1500-იანი წლებიდან იქცა, როდესაც უცნობმა მბეჭდავმა ამწყობ დაზგაზე წიგნის საცდელი ეგზემპლარი</p>', NULL, NULL, '2019-06-07 09:09:13', '2019-06-18 08:54:03');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('8', '4', 'en', 'ნინო წამალაძე', '<p>საბეჭდი და ტიპოგრაფიული ინდუსტრიის უშინაარსო ტექსტია. იგი სტანდარტად 1500-იანი წლებიდან იქცა, როდესაც უცნობმა მბეჭდავმა ამწყობ დაზგაზე წიგნის საცდელი ეგზემპლარი</p>', NULL, NULL, '2019-06-07 09:09:14', '2019-06-07 09:09:14');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('31', '16', 'ge', 'რამდენი უნდა ვიაროთ ფეხით, რომ წონაში დავიკლოთ', '<p><span style=\"font-weight: 400;\">ბოლო დროის ყველაზე გავრცელებული და მარტივი ვარჯიში სიარულია.</span></p>\r\n<p><span style=\"font-weight: 400;\">სიარულით ადამიანს საშუალოდ კვირაში 500 გრამის, 5 თვეში კი 8 კილოზე მეტის დაკლება შეუძლია.</span></p>', '<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">ბოლო დროის ყველაზე გავრცელებული და მარტივი ვარჯიში სიარულია.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">სიარულით ადამიანს საშუალოდ კვირაში 500 გრამის, 5 თვეში კი 8 კილოზე მეტის დაკლება შეუძლია.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">რა თქმა უნდა, ყველაფერი მაინც წონასა და მანძილზეა დამოკიდებული.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">ზოგადად, თუ თქვენ საშუალო ტემპით ივლით (5 კმ/სთ), ერთ საათში 400 კალორიას დაწვავთ. თუმცა, შეგიძლიათ 5 კილომეტრზე არ გაჩერდეთ და დამატებით 3 კმ გაიაროთ, რა შემთხვევაშიც კიდევ 300 კალორიას დაწვავთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">შეგიძლიათ უფრო ნაკლებიც იაროთ. მთავარია, სიარული თქვენი დღის ნაწილად აქციოთ. ამაში შეიძლება პედომეტრი (ბიჯსაზომი) დაგეხმაროთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12px;\">&nbsp;</span></p>\r\n<h1><span style=\"font-size: 20px;\"><span style=\"font-weight: 400;\">პედომეტრი</span> <span style=\"font-weight: 400;\">და</span> <span style=\"font-weight: 400;\">წონაში</span> <span style=\"font-weight: 400;\">კლება</span></span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12px;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">პედომეტრი ნაბიჯების აღმრიცხველი ხელსაწყოა, ის გამოგითვლით რამდენი ნაბიჯი გადადგით, რის მიხედვითაც გამოანგარიშებს რა მანძილი გაიარეთ და რამდენი კალორია დაწვით.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">რომ გაიგოთ, რამდენი კილომეტრი უნდა იაროთ ზედმეტი კილოგრამების დასაკლებად, ჯერ ის უნდა იცოდეთ, უკვე რამდენს გადიხართ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">მაგალითად, გაიგეთ, რომ ამჟამინდელი გრაფიკის მიხედვით დღეში 8.000 ნაბიჯს დგამთ, წონას კი მხოლოდ ინარჩუნებთ და არ იკლებთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">როდესაც ეს მონაცემები უკვე გაქვთ, მხოლოდ მარტივი მათემატიკა დაგჭირდებათ იმის გამოსათვლელად, თუ დამატებით რამდენი ნაბიჯი დაგჭირდებათ დასაკლებად.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12px;\">&nbsp;</span></p>\r\n<h1><span style=\"font-size: 20px;\"><span style=\"font-weight: 400;\">მარტივი</span> <span style=\"font-weight: 400;\">მათემატიკა</span></span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12px;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">1 კვირაში 500 გრამი რომ დაიკლოთ, დაახლოებით 3.500 კალორია უნდა დაწვათ, რაც დღეში 500 კალორიაა.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">1 კილომეტრში ადამიანი საშუალოდ 1.250 ნაბიჯს დგამს და 62.5 კალორიას წვავს.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">გამოდის, რომ დღეში 500 კალორიის დასაწვავად, 10,000 ნაბიჯი უნდა გადადგას.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">10.000 ნაბიჯი 8 კილომეტრია, რაც საკმაოდ დიდი მანძილია იმისთვის, რომ ყოველდღე მოასწროთ. თუმცა, არსებობს გზები, როგორ შეიძლება ეს რუტინა თქვენს დატვირთულ გრაფიკში რამენაირად მაინც ჩასვათ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">პირველ რიგში, გაითვალისწიენთ, რომ აუცილებელი არაა თავიდანვე 10.000 ნაბიჯით დაიწყოთ. დაიწყეთ ნაკლებით და დროთა განმავლობაში მოუმატეთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12px;\">&nbsp;</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">ჩამოდით ტრანსპორტიდან რამდენიმე გაჩერებით ადრე და დარჩენილი მანძილი ფეხით გაიარეთ;</span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">მანქანა დანიშნულებიდან ცოტა შორს გააჩერეთ;</span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400; font-size: 12px;\">არ გამოიყენოთ ლიფტი და სართულები ფეხით აიარეთ.</span></li>\r\n</ul>', 'ბოლო დროის ყველაზე გავრცელებული და მარტივი ვარჯიში სიარულია.  სიარულით ადამიანს საშუალოდ კვირაში 500 გრამის, 5 თვეში კი 8 კილოზე მეტის დაკლება შეუძლია.', '2019-06-26 10:37:25', '2019-10-07 08:53:27');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('32', '16', 'en', 'რამდენი უნდა ვიაროთ ფეხით, რომ წონაში დავიკლოთ', '<p><span style=\"font-weight: 400;\">ბოლო დროის ყველაზე გავრცელებული და მარტივი ვარჯიში სიარულია.</span></p>\r\n<p><span style=\"font-weight: 400;\">სიარულით ადამიანს საშუალოდ კვირაში 500 გრამის, 5 თვეში კი 8 კილოზე მეტის დაკლება შეუძლია.</span></p>', '<p><span style=\"font-weight: 400;\">ბოლო დროის ყველაზე გავრცელებული და მარტივი ვარჯიში სიარულია.</span></p>\r\n<p><span style=\"font-weight: 400;\">სიარულით ადამიანს საშუალოდ კვირაში 500 გრამის, 5 თვეში კი 8 კილოზე მეტის დაკლება შეუძლია.</span></p>\r\n<p><span style=\"font-weight: 400;\">რა თქმა უნდა, ყველაფერი მაინც წონასა და მანძილზეა დამოკიდებული.</span></p>\r\n<p><span style=\"font-weight: 400;\">ზოგადად, თუ თქვენ საშუალო ტემპით ივლით (5 კმ/სთ), ერთ საათში 400 კალორიას დაწვავთ. თუმცა, შეგიძლიათ 5 კილომეტრზე არ გაჩერდეთ და დამატებით 3 კმ გაიაროთ, რა შემთხვევაშიც კიდევ 300 კალორიას დაწვავთ.</span></p>\r\n<p><span style=\"font-weight: 400;\">შეგიძლიათ უფრო ნაკლებიც იაროთ. მთავარია სიარული თქვენი დღის ნაწილად აქციოთ. ამაში შეიძლება პედომეტრი (ბიჯსაზომი) დაგეხმაროთ.</span></p>\r\n<h2><span style=\"font-weight: 400;\">პედომეტრი</span> <span style=\"font-weight: 400;\">და</span> <span style=\"font-weight: 400;\">წონაში</span> <span style=\"font-weight: 400;\">კლება</span></h2>\r\n<p><span style=\"font-weight: 400;\">პედომეტრი ნაბიჯების აღმრიცხველი ხელსაწყოა, ის გამოგითვლით რამდენი ნაბიჯი გადადგით, რის მიხედვითაც გამოანგარიშებს რა მანძილი გაიარეთ და რამდენი კალორია დაწვით.</span></p>\r\n<p><span style=\"font-weight: 400;\">რომ გაიგოთ, რამდენი კილომეტრი უნდა იაროთ ზედმეტი კილოგრამების დასაკლებად, ჯერ ის უნდა იცოდეთ, უკვე რამდენს გადიხართ.</span></p>\r\n<p><span style=\"font-weight: 400;\">მაგალითად, გაიგეთ, რომ ამჟამინდელი გრაფიკის მიხედვით დღეში 8.000 ნაბიჯს დგამთ, წონას კი მხოლოდ ინარჩუნებთ და არ იკლებთ.</span></p>\r\n<p><span style=\"font-weight: 400;\">როდესაც ეს მონაცემები უკვე გაქვთ, მხოლოდ მარტივი მათემატიკაა დაგჭირდებათ იმის გამოსათვლელად, თუ დამატებით რამდენი ნაბიჯი დაგჭირდებათ დასაკლებად.</span></p>\r\n<h2><span style=\"font-weight: 400;\">მარტივი</span> <span style=\"font-weight: 400;\">მათემატიკა</span></h2>\r\n<p><span style=\"font-weight: 400;\">1 კვირაში 500 გრამი რომ დაიკლოთ, დაახლოებით 3.500 კალორია უნდა დაწვათ, რაც დღეში 500 კალორიაა.</span></p>\r\n<p><span style=\"font-weight: 400;\">1 კილომეტრში ადამიანი საშუალოდ 1.250 ნაბიჯს დგამს და 62.5 კალორიას წვავს.</span></p>\r\n<p><span style=\"font-weight: 400;\">გამოდის, რომ დღეში 500 კალორიის დასაწვავად, 10,000 ნაბიჯი უნდა გადადგას.</span></p>\r\n<p><span style=\"font-weight: 400;\">10.000 ნაბიჯი 8 კილომეტრია, რაც საკმაოდ დიდი მანძილია იმისთვის, რომ ყოველდღე მოასწროთ. თუმცა, არსებობს გზები, როგორ შეიძლება ეს რუტინა თქვენს დატვირთულ გრაფიკში რამენაირად მაინც ჩასვათ.</span></p>\r\n<p><span style=\"font-weight: 400;\">პირველ რიგში, გაითვალისწიენთ, რომ აუცილებელი არაა თავიდანვე 10.000 ნაბიჯით დაიწყოთ. დაიწყეთ ნაკლებით და დროთა განმავლობაში მოუმატეთ.</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">ჩამოდით ტრანსპორტიდან რამდენიმე გაჩერებით ადრე და დარჩენილი მანძილი ფეხით გაიარეთ;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">მანქანა დანიშნულებიდან ცოტა შორს გააჩერეთ;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">არ გამოიყენოთ ლიფტი და სართულები ფეხით აიარეთ.</span></li>\r\n</ul>', 'ბოლო დროის ყველაზე გავრცელებული და მარტივი ვარჯიში სიარულია.  სიარულით ადამიანს საშუალოდ კვირაში 500 გრამის, 5 თვეში კი 8 კილოზე მეტის დაკლება შეუძლია.', '2019-06-26 10:37:25', '2019-06-26 10:41:40');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('33', '17', 'ge', 'არის თუ არა თაფლი შაქარზე სასარგებლო', '<p><span style=\"font-weight: 400;\">როდესაც ადამიანები შაქრის მავნელობაზე საუბრობენ, იქვე აუცილებლად თაფლსაც ახსენებენ ხოლმე. ისინი დასძენენ, რომ შაქრის გამოყენება ჯანმრთელობისთვის სახიფათოა და რომ ჯობს ის თაფლით ჩავანაცვლოთ.</span></p>', '<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">როდესაც ადამიანები შაქრის მავნელობაზე საუბრობენ, იქვე აუცილებლად თაფლსაც ახსენებენ ხოლმე. ისინი დასძენენ, რომ შაქრის გამოყენება ჯანმრთელობისთვის სახიფათოა და რომ ჯობს ის თაფლით ჩავანაცვლოთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">რამდენად შეესაბამება ეს აზრი სიმართლეს?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">არც ისე.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">მოდით, ვნახოთ რატომ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">შაქარსა და თაფლში დაახლოებით ერთნაირი რაოდენობის გლუკოზა და ფრუქტოზაა. მთავარი, რაც მათ ერთმანეთისგან განასხვავებს ისაა, რომ თაფლი მონოსაქარიდებისგან შედგება, შაქარი კი &ndash; დისაქარიდებისგან.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">უფრო მარტივად რომ ვთქვათ, თაფლში გლუკოზა და ფრუქტოზა ცალ-ცალკე გვხვდება, შაქარში კი &ndash; ერთად.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">თუმცა, ეს ორგანიზმისთვის ბევრს არაფერს ცვლის.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">როდესაც ჩვენი ორგანიზმი შაქარსა და თაფლს გადაამუშავებს, საბოლოოდ ორივე შემთხვევაში ერთსა და იმავე რამეს: მონოსაქარიდებს იღებს.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">იმ დისაქარიდს, რომელიც შაქარში გვხვდება, საქაროზას (გლუკოზა და ფრუქტოზა ერთად) ვუწოდებთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">როდესაც ჩვენ შაქარს მივირთმევთ, საქაროზას ვიღებთ, ხოლო როდესაც თაფლს &ndash; გლუკოზასა და ფრუქტოზას ცალ-ცალკე. საბოლოოდ კი საქაროზაც გლუკოზამდე და ფრუქტოზამდე დაიყვანება.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">ცხადია, დისაქარიდი უნდა დაიშალოს, რათა მონოსაქარიდები მივიღოთ, ამას კი დრო სჭირდება.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">ამიტომ, თაფლი, შაქართან შედარებით უფრო სწრაფად მოგვცემს ენერგიას, ვინაიდან თაფლში გლუკოზა და ფრუქტოზა ისედაც ცალ-ცალკეა.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">ეს იმას ნიშნავს, რომ თუ ოდესმე ენერგიის სწრაფი წყარო დაგჭირდებათ და არჩევანი შაქარსა და თაფლს შორის გექნებათ, აჯობებს თაფლი აირჩიოთ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">თუმცა, გარდა ბოლო შემთხვევისა, ორგანიზმი მათ შორის განსხვავებას ვერ ხედავს. მისთვის მნიშნელობა არ აქვს, თქვენ თაფლს მიიღებთ თუ &ndash; შაქარს: ისინი მაინც ერთსა და იმავე საკვებ ნივთიერებებს შეიცავენ და საბოლოო შედეგიც ერთია.</span></p>', 'როდესაც ადამიანები შაქრის მავნელობაზე საუბრობენ, იქვე აუცილებლად თაფლსაც ახსენებენ ხოლმე. ისინი დასძენენ, რომ შაქრის გამოყენება ჯანმრთელობისთვის სახიფათოა და რომ ჯობს ის თაფლით ჩავანაცვლოთ.', '2019-06-27 11:58:46', '2019-12-18 13:08:21');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('34', '17', 'en', 'არის თუ არა თაფლი შაქარზე სასარგებლო', '<p><span style=\"font-weight: 400;\">როდესაც ადამიანები შაქრის მავნელობაზე საუბრობენ, იქვე აუცილებლად თაფლსაც ახსენებენ ხოლმე. ისინი დასძენენ, რომ შაქრის გამოყენება ჯანმრთელობისთვის სახიფათოა და რომ ჯობს ის თაფლით ჩავანაცვლოთ.</span></p>', '<p><span style=\"font-weight: 400;\">როდესაც ადამიანები შაქრის მავნელობაზე საუბრობენ, იქვე აუცილებლად თაფლსაც ახსენებენ ხოლმე. ისინი დასძენენ, რომ შაქრის გამოყენება ჯანმრთელობისთვის სახიფათოა და რომ ჯობს ის თაფლით ჩავანაცვლოთ.</span></p>\r\n<p><span style=\"font-weight: 400;\">რამდენად შეესაბამება ეს აზრი სიმართლეს?</span></p>\r\n<p><span style=\"font-weight: 400;\">არც ისე.</span></p>\r\n<p><span style=\"font-weight: 400;\">მოდით, ვნახოთ რატომ.</span></p>\r\n<p><span style=\"font-weight: 400;\">&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">შაქარსა და თაფლში დაახლოებით ერთნაირი რაოდენობის გლუკოზა და ფრუქტოზაა. მთავარი, რაც მათ ერთმანეთისგან განასხვავებს ისაა, რომ თაფლი მონოსაქარიდებისგან შედგება, შაქარი კი &ndash; დისაქარიდებისგან.</span></p>\r\n<p><span style=\"font-weight: 400;\">უფრო მარტივად რომ ვთქვათ, თაფლში გლუკოზა და ფრუქტოზა ცალ-ცალკე გვხვდება, შაქარში კი &ndash; ერთად.</span></p>\r\n<p><span style=\"font-weight: 400;\">თუმცა, ეს ორგანიზმისთვის ბევრს არაფერს ცვლის.</span></p>\r\n<p><span style=\"font-weight: 400;\">როდესაც ჩვენი ორგანიზმი შაქარსა და თაფლს გადაამუშავებს, საბოლოოდ ორივე შემთხვევაში ერთსა და იმავე რამეს: მონოსაქარიდებს იღებს.</span></p>\r\n<p><span style=\"font-weight: 400;\">&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">იმ დისაქარიდს, რომელიც შაქარში გვხვდება, საქაროზას (გლუკოზა და ფრუქტოზა ერთად) ვუწოდებთ.</span></p>\r\n<p><span style=\"font-weight: 400;\">როდესაც ჩვენ შაქარს მივირთმევთ, საქაროზას ვიღებთ, ხოლო როდესაც თაფლს &ndash; გლუკოზასა და ფრუქტოზას ცალ-ცალკე. საბოლოოდ კი საქაროზაც გლუკოზამდე და ფრუქტოზამდე დაიყვანება.</span></p>\r\n<p><span style=\"font-weight: 400;\">ცხადია, დისაქარიდი რომ დაიშალოს რათა მონოსაქარიდები მივიღოთ, ამას კი დრო სჭირდება.</span></p>\r\n<p><span style=\"font-weight: 400;\">ამიტომ, თაფლი, შაქართან შედარებით უფრო სწრაფად მოგვცემს ენერგიას, ვინაიდან თაფლში გლუკოზა და ფრუქტოზა ისედაც ცალ-ცალკეა.</span></p>\r\n<p><span style=\"font-weight: 400;\">ეს იმას ნიშნავს, რომ თუ ოდესმე ენერგიის სწრაფი წყარო დაგჭირდებათ და არჩევანი შაქარსა და თაფლს შორის გექნებათ, აჯობებს თაფლი აირჩიოთ.</span></p>\r\n<p><span style=\"font-weight: 400;\">თუმცა, გარდა ბოლო შემთხვევისა, ორგანიზმი მათ შორის განსხვავებას ვერ ხედავს. მისთვის მნიშნელობა არ აქვს, თქვენ თაფლს მიიღებთ თუ &ndash; შაქარს: ისინი მაინც ერთსა და იმავე საკვებ ნივთიერებებს შეიცავენ და საბოლოო შედეგიც ერთია.</span></p>', 'როდესაც ადამიანები შაქრის მავნელობაზე საუბრობენ, იქვე აუცილებლად თაფლსაც ახსენებენ ხოლმე. ისინი დასძენენ, რომ შაქრის გამოყენება ჯანმრთელობისთვის სახიფათოა და რომ ჯობს ის თაფლით ჩავანაცვლოთ.', '2019-06-27 11:58:46', '2019-06-27 11:58:46');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('45', '23', 'ge', 'ხანდახან, ჯანსაღად კვებაც აშლილობაა', '<p>ორთორექსია, ანორექსიასა და ბულიმიასთან შედარებით, ნაკლებად ცნობილი და აღწერილია, მაგრამ მასაც შეიძლება სერიოზული უარყოფითი შედეგები ჰქონდეს.</p>\r\n<p>ეს კვებითი აშლილობა კაცებსა და ქალებს თანაბრად ემართებათ, ვეგანებსა და ვეგეტარიანელებში კი რისკი უფრო დიდია.</p>', '<p style=\"text-align: justify;\">ორთორექსია, ანორექსიასა და ბულიმიასთან შედარებით, ნაკლებად ცნობილი და აღწერილი კვებითი აშლილობაა, მაგრამ მასაც შეიძლება სერიოზული უარყოფითი შედეგები ჰქონდეს.</p>\r\n<p style=\"text-align: justify;\">ის კაცებსა და ქალებს თანაბრად ემართებათ, ვეგანებსა და ვეგეტარიანელებში კი რისკი უფრო დიდია.</p>\r\n<p style=\"text-align: justify;\">ორთორექსიით დაავადებული ადამიანების კვება, ერთი შეხედვით, იმ ადამიანების ქცევას ჰგავს, რომლებიც ჯანმრთელობაზე ზრუნავენ.</p>\r\n<p style=\"text-align: justify;\">ორივე შემთხვევაში, ადამიანი არაჯანსაღი პროდუქტების (მაგ: კონსერვანტები, მარილი, შაქარი, ცხიმი და ცხოველური ნაწარმი) ჭამას არიდებს თავს.</p>\r\n<p style=\"text-align: justify;\">თუმცა, რაღა თქმა უნდა, ორთორექსია მეტია, ვიდრე უბრალოდ ჯანსაღი კვება.<br /> ესაა მდგომარეობა, როდესაც ადამიანი ჯანსაღად კვების იდეით უკიდურესადაა შეპყრობილი, ან, უფრო პოეტურად რომ ვთქვათ, არაჯანსაღი მისწრაფება აქვს ჯანსაღი ცხოვრების წესისკენ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">ორთორექსია, მართლაც, უწყინარი დიეტის დაცვით იწყება.</p>\r\n<p style=\"text-align: justify;\">პრობლემა ისაა, რომ რაღაც დროის შემდეგ, ჯანსაღად ცხოვრების სურვილი უმართავი ხდება და ადამიანის ცხოვრებაში პრობლემების შექმნასა და მისი მთელი ცხოვრების გაკონტროლებას იწყებს.</p>\r\n<p style=\"text-align: justify;\">კონკრეტული მომენტი, როდის გადადის კვებითი ქცევა კვებით აშლილობაში, რთული დასაჭერია, რადგან ჯანსაღ კვებასა და ორთორექსიას შორის აშკარა ზღვარი არ არსებობს.</p>\r\n<p style=\"text-align: justify;\">ყველაფერი იქამდე დაიყვანება, ზოგიერთი საკვების არჭამა ობსესია (აკვიატება) არის თუ &ndash; არა.</p>\r\n<p style=\"text-align: justify;\">ობსესიის საშიშროება დგას, თუ თქვენ:</p>\r\n<ul style=\"text-align: justify;\">\r\n<li>გადამეტებულ დროსა და ენერგიას უთმობთ იმაზე ფიქრს, თუ რა პროდუქტები იყიდოთ, მოამზადოთ და ჭამოთ;</li>\r\n<li>თქვენი ცხოვრება იმდენადაა ამ საკითხით მოცული, რომ ჩვეულებრივად ცხოვრების საშუალება აღარ აქვთ;</li>\r\n<li>იმდენად ხართ კონცენტრირებული საჭმლის წარმომავლობასა და ტიპზე, რომ თქვენთვის რთული ან შეუძლებელია ნებისმიერი ისეთი რამის მირთმევა, რაც თქვენს სახლში არ დამზადებულა.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">საბოლოო შედეგი შეიძლება კვების დეფიციტი ან წონაში კლება იყოს, რადგან ორთორექსიით დაავადებული ადამიანების კვების რაციონი თანდათან სულ უფრო ღარიბდება.</p>', 'ორთორექსია, ანორექსიასა და ბულიმიასთან შედარებით, ნაკლებად ცნობილი და აღწერილია, მაგრამ მასაც შეიძლება სერიოზული უარყოფითი შედეგები ჰქონდეს.  ეს კვებითი აშლილობა კაცებსა და ქალებს თანაბრად ემართებათ, ვეგანებსა და ვეგეტარიანელებში კი რისკი უფრო დიდია.', '2019-06-28 09:02:38', '2019-07-10 06:00:17');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('46', '23', 'en', 'ხანდახან, ჯანსაღად კვებაც აშლილობაა', '<p>ორთორექსია, ანორექსიასა და ბულიმიასთან შედარებით, ნაკლებად ცნობილი და აღწერილია, მაგრამ მასაც შეიძლება სერიოზული უარყოფითი შედეგები ჰქონდეს.</p>\r\n<p>ეს კვებითი აშლილობა კაცებსა და ქალებს თანაბრად ემართებათ, ვეგანებსა და ვეგეტარიანელებში კი რისკი უფრო დიდია.</p>', '<p>ორთორექსია, ანორექსიასა და ბულიმიასთან შედარებით, ნაკლებად ცნობილი და აღწერილი კვებითი აშლილობაა, მაგრამ მასაც შეიძლება სერიოზული უარყოფითი შედეგები ჰქონდეს.</p>\r\n<p>ეს კვებითი აშლილობა კაცებსა და ქალებს თანაბრად ემართებათ, ვეგანებსა და ვეგეტარიანელებში კი რისკი უფრო დიდია.</p>\r\n<p>ორთორექსიით დაავადებული ადამიანების კვება, ერთი შეხედვით, იმ ადამიანების ქცევას ჰგავს, რომლებიც ჯანმრთელობაზე ზრუნავენ.</p>\r\n<p>ორივე შემთხვევაში, ადამიანი არაჯანსაღი რამეების (მაგ: კონსერვანტები, მარილი, შაქარი, ცხიმი და ცხოველური ნაწარმი) ჭამას არიდებს თავს.</p>\r\n<p>თუმცა, რაღა თქმა უნდა, ორთორექსია მეტია, ვიდრე უბრალოდ ჯანსაღი კვება.<br /> ესაა მდგომარეობა, როდესაც ადამიანი ჯანსაღად კვების იდეით უკიდურესადაა შეპყრობილი, ან, უფრო პოეტურად რომ ვთქვათ, არაჯანსაღი მისწრაფება აქვს ჯანსაღი ცხოვრების წესისკენ.</p>\r\n<p>&nbsp;</p>\r\n<p>ორთორექსია, მართლაც, უწყინარი დიეტის დაცვით იწყება.</p>\r\n<p>პრობლემა ისაა, რომ რაღაც დროის შემდეგ, ჯანსაღად ცხოვრების სურვილი უმართავი ხდება და ადამიანის ცხოვრებაში პრობლემების შექმნასა და მისი მთელი ცხოვრების გაკონტროლებას იწყებს.</p>\r\n<p>კონკრეტული მომენტი, როდის გადადის კვებითი ქცევა კვებით აშლილობაში, რთული დასაჭერია, რადგან ჯანსაღ კვებასა და ორთორექსიას შორის აშკარა ზღვარი არ არსებობს.</p>\r\n<p>ყველაფერი იქამდე დაიყვანება, ზოგიერთი საკვების არჭამა ობსესია (აკვიატება) არის თუ &ndash; არა.</p>\r\n<p>ობსესიის საშიშროება დგას, თუ თქვენ:</p>\r\n<ul>\r\n<li>გადამეტებულ დროსა და ენერგიას უთმობთ იმაზე ფიქრს, თუ რა პროდუქტები იყიდოთ, მოამზადოთ და ჭამოთ;</li>\r\n<li>თქვენი ცხოვრება იმდენადაა ამ საკითხით მოცული, რომ ჩვეულებრივად ცხოვრების საშუალება აღარ აქვთ;</li>\r\n<li>იმდენად ხართ კონცენტრირებული საჭმლის წარმომავლობასა და ტიპზე, რომ თქვენთვის რთული ან შეუძლებელია ნებისმიერი ისეთი რამის მირთმევა, რაც თქვენს სახლში არ დამზადებულა.</li>\r\n</ul>\r\n<p>საბოლოო შედეგი შეიძლება კვების დეფიციტი ან წონაში კლება იყოს, რადგან ორთორექსიით დაავადებული ადამიანების კვების რაციონი თანდათან სულ უფრო ღარიბდება.</p>', 'ორთორექსია, ანორექსიასა და ბულიმიასთან შედარებით, ნაკლებად ცნობილი და აღწერილია, მაგრამ მასაც შეიძლება სერიოზული უარყოფითი შედეგები ჰქონდეს.  ეს კვებითი აშლილობა კაცებსა და ქალებს თანაბრად ემართებათ, ვეგანებსა და ვეგეტარიანელებში კი რისკი უფრო დიდია.', '2019-06-28 09:02:38', '2019-06-28 09:02:38');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('47', '24', 'ge', 'სამი მარტივი წესი პირის ღრუს ჰიგიენის დასაცავად', '<p style=\"text-align: justify;\">პირის ღრუს ჰიგიენის დაცვა მთლიანი ორგანიზმისთვის არის მნიშვნელოვანი.</p>\r\n<p style=\"text-align: justify;\">ჩვენ მოგიყვებით სამ მარტივ წესს, რომლის დაცვის შემთხვევაშიც, თავი მშვიდად შეგიძლიათ იგრძნოთ.</p>', '<p style=\"text-align: justify;\">პირის ღრუს ჰიგიენის დაცვა მთლიანი ორგანიზმისთვის არის მნიშვნელოვანი.</p>\r\n<p style=\"text-align: justify;\">ჩვენ მოგიყვებით სამ მარტივ წესს, რომლის დაცვის შემთხვევაშიც, თავი მშვიდად შეგიძლიათ იგრძნოთ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h6><span style=\"font-size: 18px;\">ნაწილი პირველი</span></h6>\r\n<p style=\"text-align: justify;\">თქვენ მხოლოდ კბილის ძაფი, ჯაგრისი და დღეში 5 წუთი დაგჭირდებათ. კბილების ყოველდღიურად გამოხეხვა და კბილის ძაფით გაწმენდა, პირიდან ბაქტერიებსა და შაქარს მოგაშორებთ. ეს დაგეხმარებათ აიცილოთ კბილების გაფუჭება. პირველი ნაწილი ყველაზე მნიშვნელოვანია პირის ღრუს ჰიგიენისთვის.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h6 style=\"text-align: justify;\"><span style=\"font-size: 18px;\">ნაწილი მეორე</span></h6>\r\n<p style=\"text-align: justify;\">აუცილებელია, რომ სტომატოლოგს გააწმენდინოთ კბილები. ისინი ისეთ ადგილებს გაასუფთავებენ, რომლებსაც თქვენ ვერ მიხედავთ (მაგ: ღრძილების ქვეშ). რეგულარული ვიზიტები საბოლოო ჯამში უფრო იაფი დაგიჯდებათ, ვიდრე უკვე შექმნილი პრობლემების გამოსწორება.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h6><span style=\"font-size: 18px;\">ნაწილი მესამე</span></h6>\r\n<p style=\"text-align: justify;\">ბევრი ადამიანი აღიარებს, რომ სტომატოლოგთან მხოლოდ მაშინ მიდის, როდესაც ატყობს, რომ რაღაც რიგზე ვერაა. ჩვენ გირჩევთ, რომ დიდხანს არ გაწელოთ ექიმთან ვიზიტი, რომ უკვე დაგვიანებული არ იყოს. რეგულარული ვიზიტების დროს ისინი უფრო კომფორტული და მარტივი იქნება. პოტენციურად საშიში პრობლემების საწყის ფაზაში მოგვარება, ბევრ დისკომფორტს აგარიდებთ თავიდან.</p>', 'პირის ღრუ ჰიგიენა კბილები ჯაგრისი გაწმენდა სტომატოლოგი', '2019-07-09 14:09:32', '2019-07-10 06:27:48');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('48', '24', 'en', 'სამი მარტივი წესი პირის ღრუს ჰიგიენის დასაცავად', NULL, NULL, NULL, '2019-07-09 14:09:32', '2019-07-09 14:09:32');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('49', '25', 'ge', 'რეკომენდაციები ძილზე', '<p><span style=\"font-weight: 400;\">ძილი თქვენი ცხოვრების ხარისხს მნიშვნელოვნად განსაზღვრავს.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-weight: 400;\">სწორედ ამიტომ, თუ ცხოვრების ხარისხის გაუმჯობესებასთან არანაირი პრობლემა არ გაქვთ, გირჩევთ, რომ ქვემოთ ჩამოთვლილი რეკომენდაციები შეძლებისდაგვარად გაითვალისწინოთ.</span></p>', '<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">ძილი თქვენი ცხოვრების ხარისხს მნიშვნელოვნად განსაზღვრავს.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">სწორედ ამიტომ, თუ ცხოვრების ხარისხის გაუმჯობესებასთან არანაირი პრობლემა არ გაქვთ, გირჩევთ, რომ ქვემოთ ჩამოთვლილი რეკომენდაციები შეძლებისდაგვარად გაითვალისწინოთ.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">ეს რჩევები აშშ-ის ძილის ეროვნული ფონდის (NSF) მიერ არის ჩამოყალიბებული.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-weight: 400;\">ყოველთვის (შაბათ-კვირასაც კი) ერთსა და იმავე დროს დაიძინეთ და გაიღვიძეთ. ეს თქვენს ორგანიზმს ბიოლოგიური საათის რეგულირებაში ეხმარება. შედეგად, თქვენ უფრო მარტივად და კარგად დაიძინებთ.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">თქვენი სხეული ძილის რეჟიმზე უნდა გადაერთოს. ასე რომ, დაძინებამდე ერთი საათი დამამშვიდებელ აქტივობებს (მაგალითად, კითხვას) დაუთმეთ. უმჯობესია, თუ ეს აქტივობა ტელეფონებთან და სხვა მსგავს მოწყობილობებთან არ იქნება დაკავშირებული, რადგან ლურჯი ნათება ტვინს ააქტიურებს და ჩაძინებას ართულებს.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">ივარჯიშეთ ყოველდღე. უკეთესი იქნება, თუ დატვირთულ ვარჯიშებს გააკეთებთ, თუმცა, თუ ამას ვერ მოახერხებთ, მსუბუქად ვარჯიშსაც არაუშავს. ივარჯიშეთ დღის ნებისმიერ მონაკვეთში, მაგრამ ძილის მაგივრად &ndash; არა!<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">შექმენით ძილისთვის შესაბამისი პირობები თქვენს ოთახში. იქ ტემპერატურა დაბალი უნდა იყოს, დაახლოებით 16-20 გრადუსი. ოთახში არ უნდა იყოს არაფერი, რაც გამოგაფხიზლებთ ან დაძინებაში შეგიშლით ხელს. მაგალითად, თუ თქვენი პარტნიორი ხვრინავს, სხვა ოთახში შეიძლება არ გაამწესოთ ის, მაგრამ თქვენ თავზე მაინც უნდა იზრუნოთ და ყურის საცობები გაიკეთოთ ან თეთრი ხმაურის მოწყობილობა დაიდგათ.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">დაიძინეთ კომფორტულ მატრასსა და ბალიშზე. მატრასი, რომელსაც წლებია იყენებთ, თავის მოვალეობას შეიძლება კარგად ვეღარ ასრულებდეს. კარგი მატრასის &ldquo;სიცოცხლის&rdquo; ხანგრძლივობა საშუალოდ 9-10 წელია.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">გამოიყენეთ სინათლე დღეღამური (ცირკადული) რიტმის მოსაწესრიგებლად. მოერიდეთ ნათელ შუქს საღამოს და პირიქით, არ მოერიდოთ დილის საათებში. ეს თქვენს დღეღამურ ციკლს დაარეგულირებს.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">მოერიდეთ ალკოჰოლს, სიგარეტს, კოფეინსა და მძიმე საკვებს საღამოს. მათ თქვენი ძილის დაფრთხობა შეუძლიათ. ბევრის ან ცხარეს ჭამამ ძილის წინ შეიძლება მოუნელებლობის დისკომფორტი გამოიწვიოს, რაც ძილს გაგირთულებთ. სასურველია, რომ ძილამდე 2-3 საათი ბევრი არ ჭამოთ. თუ ძალიან გშიათ, დაძინებამდე 45 წუთით ადრე მსუბუქად მიირთვით.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">თუ მაინც არ გეძინებათ, ადექით საწოლიდან, გადით სხვა ოთახში და სანამ დაღლილად იგრძნობთ თავს, იქ გააკეთეთ რამე დამამშვიდებელი. ოთახში, სადაც გძინავთ, აჯობებს, თუ სამუშაო ნივთები, კომპიუტერები და ტელევიზორები არ იქნება. ამ დროს საძინებელი ოთახისა და ძილის ერთმანეთთან ასოციაცია გაიზრდება და ეს დაძინებას გაგიმარტივებთ.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">მოერიდეთ დღის განმავლობაში თვლემას, თუ ღამით დაძინების პრობლემა გაქვთ. შეიძლება ჩათვლემა დღის ბოლომდე მიყვანაში გეხმარებოდეთ, მაგრამ მისმა ამოღებამ შესაძლოა ღამის ძილი გაგიუმჯობესოთ.<br /><br /></span></li>\r\n<li style=\"font-weight: 400; text-align: justify;\"><span style=\"font-weight: 400;\">თუ მაინც გიჭირთ დაძინება, არ მოგერიდოთ ექიმთან ამაზე საუბრის.</span></li>\r\n</ul>', 'ძილის რეკომენდაცია, როგორ დავიძინო სწორად, სწორი ძილი, ვერ ვიძინებ, მეღვიძება, ინსომნია', '2019-07-29 13:32:26', '2019-09-25 11:29:58');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('50', '25', 'en', 'რეკომენდაციები ძილზე', '<p><span style=\"font-weight: 400;\">ძილი თქვენი ცხოვრების ხარისხს მნიშვნელოვნად განსაზღვრავს.</span></p>\r\n<p><span style=\"font-weight: 400;\">აქედან გამომდინარე, თუ ცხოვრების ხარისხის გაუმჯობესებასთან არანაირი პრობლემა არ გაქვთ, გირჩევდით, რომ ქვემოთ ჩამოთვლილი რეკომენდაციები მაქსიმალურად გაითვალისწინოთ.</span></p>', '<p><span style=\"font-weight: 400;\">ძილი თქვენი ცხოვრების ხარისხს მნიშვნელოვნად განსაზღვრავს.</span></p>\r\n<p><span style=\"font-weight: 400;\">აქედან გამომდინარე, თუ ცხოვრების ხარისხის გაუმჯობესებასთან არანაირი პრობლემა არ გაქვთ, გირჩევდით, რომ ქვემოთ ჩამოთვლილი რეკომენდაციები მაქსიმალურად გაითვალისწინოთ.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-weight: 400;\">ეს რჩევები აშშ-ის ძილის ეროვნული ფონდის (NSF) მიერ არის ჩამოყალიბებული.</span></p>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">ყოველთვის, შაბათ-კვირასაც კი, ერთსა და იმავე დროს დაიძინეთ და გაიღვიძეთ. ეს თქვენს ორგანიზმს ბიოლოგიური საათის რეგულირებაში ეხმარება, თქვენ კი დაძინებას გაგიმარტივებთ და, ამავდროულად, ძილის ხარისხს გაგიუმჯობესებთ.</span></li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><em><em><span style=\"font-weight: 400;\">ძილის წინ დაკავდით დამამშვიდებელი რიტუალით. ეს რიტუალი ლურჯი გამოსხივებისგან შორს უნდა იყოს, რადგან წილის წინ უმჯობესია ისეთ აქტივობებს მოერიდოთ, რომლებიც შფოთვას, სტესს და აღელვებას იწვევს, რათა უფრო მარტივად დაიძინოთ და მშვიდად გეძინოთ.</span></em></em></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">ივარჯიშეთ ყოველდღე. უმჯობესია დატვირთული ვარჯიშები აკეთოთ, მაგრამ თუ ვერ მოახერხებთ, სულ უვარჯიშობას, მსუბუქი ვარჯიშიც აჯობებს. ივარჯიშეთ დღის ნებისმიერ მონაკვეთში, მაგრამ ძილის მაგივრად - არა!</span></li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">შექმენით ძილისთვის შესაბამისი პირობები თქვენს ოთახში. იქ ტემპერატურა დაბალი უნდა იყოს, დაახლოებით 16-20 გრადუსი. ოთახში არ უნდა იყოს არაფერი, რისი ხმასაც თქვენი ძილის გაწყვეტას გამოიწვევს. ოთახში არ უნდა იყოს სინათლე და, ზოგადად, არაფერი, რაც გამოგაფხიზლებთ. ან, თუ თქვენი პარტნიორი ხვრინავს, სხვა ოთახში შეიძლება არ გაამწესოთ ის, მაგრამ თქვენს თავზე იზრუნოთ და ყურის საცობები გაიკეთოთ ან თეთრი ხმაურის მოწყობილობა დაიდგათ.</span></li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">დაიძინეთ კომფორტულ მატრასსა და ბალიშზე. მატრასი, რომელსაც წლებია იყენებთ, თავის მოვალეობას შეიძლება კარგად ვეღარ ასრულებდეს. კარგი მატრასის &ldquo;სიცოცხლის&rdquo; ხანგრძლივობა საშუალოდ 9-10 წელია.</span></li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">მოაწესრიგეთ გამოიყენეთ ნათელი შუქი დღეღამური/ცირკადული რიტმის მოსაწესრიგებლად. მოერიდეთ ნათელ შუქს საღამოს და პირიქით, არ მოერიდოთ დილის საათებში. ეს თქვენს დღეღამურ ციკლს დაარეგულირებს.</span></li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">იმ შემთხვევაში, თუ დაძინების პრობლემები გაქვთ, პატარ-პატარა ჩაძინებებს მოერიდეთ, განსაკუთრებით დღის მეორე ნახევარში. შეიძლება ეს ძილი დღის ბოლომდე მიყვანაში გეხმარებოდეთ, მაგრამ თუ ღამით დაძინება გიჭირთ ხოლმე, დღის წესრიგიდან თვლემის ამოღებამ შეიძლება გიშველოთ.</span></li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<ol>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">მოერიდეთ ალკოჰოლს, სიგარეტს, კოფეინსა და მძიმე საკვებს საღამოს. მათ თქვენი ძილის დაფრთხობა შეუძლიათ. ბევრის ან ცხარეს ჭამამ ძილის წინ შეიძლება დისკომფორტი გამოიწვიოს მოუნელებლობისგან, რაც ძილს გაგირთულებთ. სასურველია, რომ ძილამდე 2-3 საათი ბევრი არ ჭამოთ. თუ ძალიან გშიათ, დაძინებამდე 45 წუთით ადრე მიირთვით მსუბუქად.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">დამშვიდდით. თქვენი სხეული ძილის რეჟიმზე უნდა გადაერთოს, ასე რომ დაძინებამდე ერთი საათი დამამშვიდებელ აქტივობებს (მაგ. კითხვა) დაუთმეთ. ზოგიერთი ადამიანისთვის ელექტრონული მოწყობილობა ჩაძინებას ურთულებს, რადგან მათი განათება ტვინს ააქტიურებს. თუ დაძინება გიჭირთ, დაძინებამდე და შუაღამეს არ გამოიყენოთ ეს მოწყობილობები.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">თუ არ გეძინებათ, გადით სხვა ოთახში და იქ გააკეთეთ რამე დამამშვიდებელი, სანამ დაღლილად არ იგრძნობთ თავს. უმჯობესია, სამუშაო ნივთები, კომპიუტერები და ტელევიზორები არ იყოს იმ ოთახში სადაც გძინავთ. საწოლი მხოლოდ დასაძინებლად და სექსისთვის გამოიყენოთ, რათა გააძლიეროთ საწოლისა და ძილის ერთმანეთთან ასოციაცია. თუ რომელიმე კონკრეტულ აქტივობას უკავშირებთ ძილთან დაკავშირებულ შფოთვას, აუცილებლად მოაშორეთ ის დაძინების რუტინისგან.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">თუ მაინც გიჭირთ დაძინება, არ მოგერიდოთ თქვენს ექიმთან ამაზე საუბრის.</span></li>\r\n</ol>\r\n<p>&nbsp;</p>', NULL, '2019-07-29 13:32:26', '2019-07-29 13:32:26');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('51', '26', 'ge', 'ალკოჰოლის რა რაოდენობა არის „ზომაზე მეტი“', '<p>ალკოჰოლს მხოლოდ განსაკუთრებულ შემთხვევებში თუ მოიხმართ, ეს, დიდი ალბათობით, არაფერს დაგიზიანებთ.</p>\r\n<p>თუმცა, ალბათ, თქვენც კარგად იცით, რომ ხშირად დალევა ფიზიკურ და ფსიქიკურ ჯანმრთელობაზე უარყოფითად აისახება.</p>', '<p style=\"text-align: justify;\">ალკოჰოლს მხოლოდ განსაკუთრებულ შემთხვევებში თუ მოიხმართ, ეს, დიდი ალბათობით, არაფერს დაგიზიანებთ.</p>\r\n<p style=\"text-align: justify;\">თუმცა, ალბათ, თქვენც კარგად იცით, რომ ხშირად დალევა ფიზიკურ და ფსიქიკურ ჯანმრთელობაზე უარყოფითად აისახება.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">საინტერესოა, სად გადის ზღვარი &bdquo;ათასში ერთხელ&ldquo; დალევასა და ალკოჰოლის პრობლემატურ მოხმარებას შორის.</p>\r\n<hr />\r\n<p style=\"text-align: justify;\">ერთი სტანდარტული სასმელი სხვადასხვა ქვეყანაში სხვადასხვა რაოდენობის სუფთა ალკოჰოლს შეიცავს.</p>\r\n<p style=\"text-align: justify;\">მაგალითად, აშშ-ის სტანდარტულ სასმელში 14 გრამი სუფთა ალკოჰოლია (355მლ ლუდი ან 150მლ ღვინო).</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">რეკომენდებული კი არის, რომ ასეთი სტანდარტის პირობებში, ქალმა მაქსიმუმ 1, კაცმა კი მაქსიმუმ 2 სასმელი მიიღოს დღეში.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">განსხვავებული დოზა ქალებისა და კაცების შემთხვევაში ფერმენტების გამოა: ორი მთავარი ფერმენტი, რაც ალკოჰოლის გადამუშავებას უზრუნველყოფს, ალკოჰოლდეჰიდროგენაზა (ADH) და ალდეჰიდდეჰიდროგენაზაა (ALDH). ზოგადად, ქალებს ADH-ის უფრო დაბალი აქტივობა აქვთ, ვიდრე კაცებს. ამის გამო, ქალის ორგანიზმი უფრო ნელა გადაამუშავებს ალკოჰოლს.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><a href=\"https://www.arcr.niaaa.nih.gov/arcr391/article02.htm\" target=\"_blank\">კვლევის</a> მიხედვით, აღნიშნული რაოდენობით დალევის დროს, ადამიანთა მხოლოდ 2%-ს ექმნება ალკოჰოლით გამოწვეული პრობლემები. ამ დოზაზე მეტი კი ზოგადად მავნეა.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">უნდა აღვნიშნოთ, რომ მთლიანი კვირის მაქსიმალური რაოდენობა რამდენიმე დღეზე არ შეიძლება გადაანაწილოთ.&nbsp;ანუ, მაგალითად, კვირაში 5 დღე თუ საერთოდ არ დალევთ იმის გამო, რომ დარჩენილ 2 დღეს ბევრი დალიოთ, უკვე ალკოჰოლის მოხმარებასთან&nbsp;დაკავშირებულ პრობლემასთან შეიძლება გვქონდეს საქმე. განსაკუთრებით ყურადსაღები&nbsp;ეს მაშინაა, თუ ქალი 4 ან მეტ, კაცი კი 5 ან მეტ სტანდარტულ სასმელს სვამს ერთ დალევაზე.</p>\r\n<hr />\r\n<p style=\"text-align: justify;\">საბოლოო ჯამში, შეგვიძლია დავასკვნათ, რომ ზომიერად დალევას უმეტესად \"არაფერი უშავს\", მაგრამ&nbsp;ჭარბი მოხმარება ჯანმრთელობისთვის ძალიან საზიანოა.</p>', 'ალკოჰოლი, დალევა, დოზა, ბევრი, ალკოჰოლიზმი, პრობლემა, დამოკიდებულება, ლოთობა', '2019-08-12 12:33:56', '2019-09-20 06:33:49');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('52', '26', 'en', 'ალკოჰოლის რა რაოდენობა არის „ბევრი“', '<p>ალკოჰოლს თუ მხოლოდ განსაკუთრებულ შემთხვევებში მოიხმართ, ეს, დიდი ალბათობით, არაფერს დაგიზიანებთ. თუმცა, ალბათ, თქვენც კარგად იცით, რომ ხშირად დალევას კარგი არაფერი მოჰყვება. საინტერესოა, სად გადის ზღვარი &bdquo;ათასში ერთხელ&ldquo; დალევასა და ალკოჰოლის პრობლემატურ მოხმარებას შორის.</p>\r\n<p>&nbsp;</p>', '<p>ალკოჰოლს თუ მხოლოდ განსაკუთრებულ შემთხვევებში მოიხმართ, ეს, დიდი ალბათობით, არაფერს დაგიზიანებთ. თუმცა, ალბათ, თქვენც კარგად იცით, რომ ხშირად დალევას კარგი არაფერი მოჰყვება. საინტერესოა, სად გადის ზღვარი &bdquo;ათასში ერთხელ&ldquo; დალევასა და ალკოჰოლის პრობლემატურ მოხმარებას შორის.</p>\r\n<p>ერთი სტანდარტული სასმელი სხვადასხვა ქვეყანაში სხვადასხვა რაოდენობის სუფთა ალკოჰოლს შეიცავს, თუმცა, შეგვიძლია აშშ-ის მაგალითი განვიხილოთ.</p>\r\n<p>აშშ-ში ერთი სტანდარტული სასმელი (მაგალითად, 355 მლ ლუდი, ან 150 მლ ღვინო) 14 გრამ სუფთა ალკოჰოლს შეიცავს. რეკომენდებული კი არის, რომ ასეთი სტანდარტის პირობებში, ქალმა მაქსიმუმ 1, კაცმა კი მაქსიმუმ 2 სასმელი მიიღოს.</p>\r\n<p>განსხვავებული დოზა ქალებისა და კაცების შემთხვევაში ფერმენტების გამოა.</p>\r\n<p>ორი მთავარი ფერმენტი, რაც ალკოჰოლის გადამუშავებას უზრუნველყოფს, ალკოჰოლდეჰიდროგენაზა (ADH) და ალდეჰიდდეჰიდროგენაზაა (ALDH).</p>\r\n<p>ზოგადად, ქალებს უფრო დაბალი ADH-ის აქტივობა აქვთ, ვიდრე კაცებს. ამის გამო, ქალების ორგანიზმი უფრო ნელა გადაამუშავებს ალკოჰოლს. ეს არის იმის მიზეზიც, რომ ქალები უფრო მოწყვლადები არიან ალკოჰოლის ეფექტის მიმართ.</p>', NULL, '2019-08-12 12:33:56', '2019-08-12 12:33:56');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('53', '27', 'ge', 'რა მოსდის ტვინს, როდესაც მთელი დღე სერიალებს უყურებთ', '<p style=\"text-align: justify;\">ბევრი ადამიანისთვის, მთელი დღე სახლში დარჩენა, კომფორტულ ტანსაცმელში კოტრიალი და სერიალების ყურება რელაქსაციის საუკეთესო მეთოდია.</p>', '<p style=\"text-align: justify;\">ბევრი ადამიანისთვის, მთელი დღე სახლში დარჩენა, კომფორტულ ტანსაცმელში კოტრიალი და სერიალების ყურება რელაქსაციის საუკეთესო მეთოდია.</p>\r\n<p style=\"text-align: justify;\">თუმცა, უნდა ვიცოდეთ, რომ ამ დროს შეიძლება ტვინსა და სხეულზე საზიანო ცვლილებებს ვიწვევდეთ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">როდესაც ბევრ ეპიზოდს გადაბმულად ვუყურებთ, დიდი ხნის განმავლობაში ვსხედვართ, იზოლირებულები ვართ სოციალური აქტივობებისგან და ბევრ არაჯანსაღ საკვებს ვიღებთ. ეს პრობლებაა, რადგან ამ დამაზიანებელი ჩვევებით, ჯანსაღ აქტივობებს ვანაცვლებთ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">თუ უკვე ფიქრობთ ამ მავნე ჩვევისთვის თავის დანებებაზე, კარგი ამბავიც გვაქვს: აუცილებელი არ არის, ეს ერთბაშად გააკეთოთ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">მაგალითად, თავდაპირველად, შეგიძლიათ შემდეგი რეკომენდაციები გაითვალისწინოთ:</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<ul>\r\n<li style=\"text-align: justify;\">დარწმუნდით, რომ სერიალების ყურება ძილში არ გიშლით ხელს. სანამ ყურებას დაიწყებდეთ, განსაზღვრეთ, რომელზე დაძინება დაგჭირდებათ იმისთვის, რომ დილით კარგად გამოძინებული იყოთ.</li>\r\n<li>წაივარჯიშეთ. ვარჯიში მნიშვნელოვანია ნეიროგენეზისთვის, ანუ ტვინში ახალი ახალი უჯრედებისა და კავშირების წარმოსაქმნელად. შეგიძლიათ, სანამ სერიალებს &bdquo;ჩაუჯდებოდეთ&ldquo;, უბანში გაისეირნოთ და ყოველი ეპიზოდის ხუთ წუთს ფეხზე დამდგარმა უყუროთ.</li>\r\n<li>უყურეთ მეგობრებთან ერთად. როდესაც სერიალს 6 საათის განმავლობაში მარტოები უყურებთ, ეს დრო სხვა ადამიანებთან ინტერაქციის საათებს აკლდება. მეგობრებისგან და ახლობლებისგან ნაკლებად იზოლირებული იყავით, რათა დეპრესიასა და სხვა ფსიქიკურ პრობლემებს მაქსიმალურად აარიდოთ თავი.</li>\r\n<li>თუ ყურების დროს წახემსება გიყვართ, აირჩიეთ ჯანსაღი ხემსი. მაგალითად, შეგიძლიათ ჰუმუსი და ბოსტნეული ან ხილი მიირთვათ. უმჯობესია, თუ მარილიან და ცხიმიან საკვებს თავს აარიდებთ.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">ამ რეკომენდაციების გაითვალისწინების შემთხვევაში, ხანდახან ისევ შეძლებთ სერიალის ერთბაშად ყურების სიამოვნების მიღებას. ოღონდ, ამჯერად იმ დარდის გარეშე, რომ შეიძლება ტვინს აზიანდებდეთ.</p>', NULL, '2019-08-15 07:19:12', '2019-08-16 13:58:06');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('54', '27', 'en', 'რა მოსდის ტვინს, როდესაც მთელი დღე სერიალებს უყურებთ', NULL, NULL, NULL, '2019-08-15 07:19:12', '2019-08-15 07:19:12');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('55', '28', 'ge', 'რატომ ვამთქნარებთ?', '<p>ადამიანები ბევრ სხვადასხვა სიტუაციაში ვამთქნარებთ.</p>\r\n<p>მაგალითად, როდესაც დაღლილები ვართ, ახალგაღვძებულზე, მოწყენილზე, შეშფოთებულზე, მშიერზე, როცა ახალ აქტივობას ვიწყებთ ან მაშინ, როდესაც სხვა ამთქნარებს.</p>', '<p>ადამიანები ბევრ სხვადასხვა სიტუაციაში ვამთქნარებთ. მაგალითად,</p>\r\n<ul>\r\n<li>როდესაც დაღლილები ვართ</li>\r\n<li>ახალგაღვძებულზე</li>\r\n<li>მოწყენილზე</li>\r\n<li>შეშფოთებულზე</li>\r\n<li>მშიერზე</li>\r\n<li>როცა ახალ აქტივობას ვიწყებთ</li>\r\n<li>როდესაც სხვა ამთქნარებს</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>თქვენ კი ახლაც შეიძლება დაამთქნაროთ, რადგან ამ საკითხზე ტექსტის წაკითხვაც, მისი ერთ-ერთი გამომწვევია.</p>\r\n<p>&nbsp;</p>\r\n<p>მთქნარებას ბევრი გამომწვევი მიზეზი აქვს. თუმცა, მისი ფიზიოლოგიური დატვირთვა დღემდე გამოცანად რჩება. ამ ეტაპზე დანამდვილებით მხოლოდ იმის თქმა შეგვიძლია, რომ რეალურად არ ვიცით, რატომ ვამთქნარებთ.</p>\r\n<p>&nbsp;</p>\r\n<p>დაახლოებით 30 წლის წინ ფიქრობდნენ, მთქნარების მიზეზი ჟანგბადის დონის გაზრდა იყო სისხლში. თუმცა, ეს ჰიპოთეზა 1987 წელს გამოქვეყნებული <a href=\"https://www.ncbi.nlm.nih.gov/pubmed/?term=Provine+R%2C+Tate+B%2C+Geldmacher%2C+yawning\" target=\"_blank\"><span style=\"color: #ff6600;\">ექსპერიმენტების</span> </a>შედეგად, უგულებელყოფილია.</p>\r\n<p>&nbsp;</p>\r\n<p>ერთ-ერთი თანამედროვე თეორია მას ტემპერატურის დარეგულირებით ხსნის. ამ თეორიის მიხედვით, მთქნარება ტვინის გამაგრილებელი მექანიზმია. გაგრილება კი იმიტომ შეიძლება დასჭირდეს ტვინს, რომ როდესაც სხეულის ტემპერატურა თბილია, დაღლილად და ძილისპირულად ვგრძნობთ თავს.</p>\r\n<p>&nbsp;</p>\r\n<p>მთქნარებაზე ერთი რამ, რაც სიზუსტით შეგვიძლია ვთქვათ, არის, რომ ის გადამდებია. ამბობენ, რომ რაც უფრო ემპათიურია ადამიანი, მასზე მით უფრო მეტად მოქმედებს სხვისი მთქნარების დანახვა. როდესაც ადამიანი სხვის დამთქნარებას ხედავს, ტვინის ის უბნები უაქტიურდება, რომლებიც სოციალურ ფუნქციებში მონაწილეობენ. საინტერესოა, რომ გადამდები არა მხოლოდ ადამიანებისთვის, არამედ სხვა ცხოველებისთვისაც არის. მაგალითად, როდესაც ძაღლები ადამიანის მთქნარებას ხედავენ, ხანდახან თავადაც ამთქნარებენ.</p>', NULL, '2019-09-12 13:02:38', '2019-09-20 13:45:31');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('56', '28', 'en', 'რატომ ვამთქნარებთ?', '<p>ადამიანები ბევრ სხვადასხვა სიტუაციაში ვამთქნარებთ.</p>\r\n<p>მაგალითად, როდესაც დაღლილები ვართ, ახალგაღვძებულზე, მოწყენილზე, შეშფოთებულზე, მშიერზე, როცა ახალ აქტივობას ვიწყებთ ან მაშინ, როდესაც სხვა ამთქნარებს.</p>', '<p>ადამიანები ბევრ სხვადასხვა სიტუაციაში ვამთქნარებთ.</p>\r\n<p>მაგალითად, როდესაც დაღლილები ვართ, ახალგაღვძებულზე, მოწყენილზე, შეშფოთებულზე, მშიერზე, როცა ახალ აქტივობას ვიწყებთ ან მაშინ, როდესაც სხვა ამთქნარებს.</p>\r\n<p>თქვენ კი ახლაც შეიძლება დაამთქნაროთ, რადგან ამ საკითხზე ტექსტის წაკითხვაც, მისი ერთ-ერთი გამომწვევია.</p>\r\n<p>მთქნარებას ბევრი გამომწვევი მიზეზი აქვს. თუმცა, მისი ფიზიოლოგიური დატვირთვა დღემდე გამოცანად რჩება.</p>\r\n<p>ამ ეტაპზე დანამდვილებით მხოლოდ იმის თქმა შეგვიძლია, რომ რეალურად არ ვიცით, რატომ ვამთქნარებთ.</p>\r\n<p>დაახლოებით 30 წლის წინ ფიქრობდნენ, სისხლში ჟანგბადის დონის გასაზრდელად ვამთქნარებდით. თუმცა, &nbsp;ეს ჰიპოთეზა 1987 წელს გამოქვეყნებული ექსპერიმენტების შედეგად, უგულებელყოფილია.</p>\r\n<p>ერთ-ერთი თანამედროვე თეორია მას ტემპერატურის დარეგულირებით ხსნის. ამ თეორიის მიხედვით, მთქნარება ტვინის გამაგრილებელი მექანიზმია. გაგრილება კი იმიტომ შეიძლება დასჭირდეს ტვინს, რომ როდესაც სხეულის ტემპერატურა თბილია, დაღლილად და ძილისპირულად ვგრძნობთ თავს.</p>\r\n<p>მთქნარებაზე ერთი რამ, რაც სიზუსტით შეგვიძლია ვთქვათ, არის, რომ ის გადამდებია.</p>\r\n<p>ამბობენ, რომ რაც უფრო ემპათიურია ადამიანი, მასზე მით უფრო მეტად მოქმედებს სხვისი მთქნარების დანახვა. როდესაც ადამიანი სხვის დამთქნარებას ხედავს, ტვინის ის უბნები უაქტიურდება, რომლებიც სოციალურ ფუნქციებში მონაწილეობენ.</p>\r\n<p>აღსანიშნავია, რომ გადამდები არა მხოლოდ ადამიანებისთვის, არამედ სხვა ცხოველებისთვისაც არის. მაგალითად, როდესაც ძაღლები ადამიანის მთქნარებას ხედავენ, ხანდახან თავადაც ამთქნარებენ.</p>', NULL, '2019-09-12 13:02:38', '2019-09-12 13:02:38');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('57', '29', 'ge', 'მითი ალუმინის შემცველი დეოდორანტის შესახებ', '<p>დეოდორანტებზე პანიკა 2000-იანი წლებიდან დაიწყო.</p>\r\n<p>ამ დროს გაჩნდა აზრი, რომ დეოდორანტები (განსაკუთრებით კი, ალუმინის შემცველი) კიბოს გაჩენის რისკს ზრდიდა.</p>', '<p>დეოდორანტებზე პანიკა 2000-იანი წლებიდან დაიწყო.</p>\r\n<p>ამ დროს გაჩნდა აზრი, რომ დეოდორანტები (განსაკუთრებით კი, ალუმინის შემცველი) კიბოს გაჩენის რისკს ზრდიდა.</p>\r\n<p>&nbsp;</p>\r\n<p>ყურადღება განსაკუთრებით მკერდის კიბოზე მახვილდებოდა; გაიზარდა მკერდის კიბოს დეორანტებთან ასოციაცია.</p>\r\n<p>&nbsp;</p>\r\n<p>ადამიანები ამტკიცებდნენ, რომ მკერდის კიბო უმეტესად იღლიასთან ახლოს ჩნდებოდა. ისინი ამ ამბავს დეოდორანტებს უკავშირებდნენ, რადგან სწორედ იღლიაში ვისვამთ/ვისხამთ მათ.</p>\r\n<p>&nbsp;</p>\r\n<p>თუ გავითვალისწინებთ, რომ ცხოვრების განმავლობაში სტატისტიკურად 8 ქალიდან 1-ს უვითარდება მკერდის კიბო, აზრი, რომ მას დეოდორანტმა, პროდუქტმა, რომელსაც ყოველდღიურად ვიყენებთ, ხელი შეიძლება შეუწყოს, ფრიად სერიოზულად ჟღერს.</p>\r\n<p>&nbsp;</p>\r\n<p>თუმცა, ეს წარმოდგენა სიმართლეს არ შეესაბამება.</p>\r\n<p>&nbsp;</p>\r\n<p>საბედნიეროდ, დეოდორანტის გარშემო ატეხილი პანიკა არ ეფუძნებოდა არაფერ რეალურს და, სინამდვილეში, არანაირი დამადასტურებელი საბუთი, რომ ის (თუნდაც, ალუმინიანი) კიბოს ან სხვა დაავადებას იწვევს, არ არსებობს.</p>\r\n<p>&nbsp;</p>\r\n<p>თუმცა, ბევრს დღემდე ჰგონია, რომ დეოდორანტი თუ არა, ალუმინი მაინც არის საშიში.</p>\r\n<p>&nbsp;</p>\r\n<p>ამ აზრმა, ბუნებრივია, საქართველომდეც მოაღწია. მაგალითად, ჯანმრთელობის ჯგუფებში ხშირად წაიკითხავთ, რომ დეოდორანტის ყიდვის დროს აუცილებლად უნდა დავაკვირდეთ მათ შემადგენლობას და ვიყიდოთ მხოლოდ ისეთები, რომლებიც ალუმინს არ შეიცავენ.</p>\r\n<p>&nbsp;</p>\r\n<p>თუმცა, გვინდა გაუწყოთ, რომ თუ მაინცდამაინც ალერგია არ გაქვთ ალუმინზე, შეგიძლიათ ნებისმიერი დეოდორანტი მშვიდად შეიძინოთ.</p>\r\n<p>&nbsp;</p>', NULL, '2019-09-20 10:00:29', '2019-09-20 13:40:59');
INSERT INTO `article_languages` (`id`, `article_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('58', '29', 'en', 'მითი ალუმინის შემცველი დეოდორანტის შესახებ', '<p>დეოდორანტებზე პანიკა 2000-იანი წლებიდან დაიწყო.</p>\r\n<p>ამ დროს გაჩნდა აზრი, რომ დეოდორანტები (განსაკუთრებით კი, ალუმინის შემცველი დეოდორანტები) კიბოს გაჩენის რისკს ზრდიდა.</p>', '<p>დეოდორანტებზე პანიკა 2000-იანი წლებიდან დაიწყო.</p>\r\n<p>ამ დროს გაჩნდა აზრი, რომ დეოდორანტები (განსაკუთრებით კი, ალუმინის შემცველი დეოდორანტები) კიბოს გაჩენის რისკს ზრდიდა.</p>\r\n<p>განსაკუთრებული ყურადღება მკერდის კიბოზე მახვილდებოდა, გაიზარდა მკერდის კიბოს დეორანტებთან ასოციაცია.</p>\r\n<p>ადამიანები ამტკიცებდნენ, რომ მკერდის კიბო უმეტესად იღლიასთან ახლოს ჩნდებოდა. ისინი ამ ამბავს დეოდორანტებს უკავშირებდნენ, რადგან სწორედ იღლიაში ვისვამთ/ვისვამთ მათ.</p>\r\n<p>თუ გავითვალისწინებთ, რომ სტატისტიკურად 8 ქალიდან 1-ს მკერდის კიბო უვითარდება ცხოვრების განმავლობაში, აზრი, რომ მას დეოდორანტმა, პროდუქტმა, რომელსაც ყოველდღიურად ვიყენებთ, ხელი შეიძლება შეუწყოს, ფრიად სერიოზულად ჟღერს.</p>\r\n<p>თუმცა, ეს წარმოდგენა სიმართლეს არ შეესაბამება.</p>\r\n<p>საბედნიეროდ, დეოდორანტის გარშემო ატეხილი პანიკა არ ეფუძნებოდა არაფერ რეალურს და, სინამდვილეში, არანაირი დამადასტურებელი საბუთი, რომ დეოდორანტი (თუნდაც, ალუმინიანი) კიბოს ან სხვა დაავადებას იწვევს, არ არსებობს.</p>\r\n<p>ასე რომ, შეგიძლიათ მშვიდად შეიძინოთ ნებისმიერი დეოდორანტი, რომელიც გაგიხარდებათ, თუ, რა თქმა უნდა, ალუმინზე ალერგია არ გაქვთ.</p>\r\n<p>თუმცა, ბევრს დღემდე ჰგონია, რომ ალუმინი საშიშია.</p>\r\n<p>ამ აზრმა, ბუნებრივია, საქართველომდეც მოაღწია. მაგალითად, ჯანმრთელობის ჯგუფებში ხშირად წაიკითხავთ, რომ დეოდორანტის ყიდვის დროს აუცილებლად უნდა დავაკვირდეთ მათ შემადგენლობას და ვიყიდოთ მხოლოდ ის, რომელიც ალუმინს არ შეიცავს.</p>', NULL, '2019-09-20 10:00:29', '2019-09-20 10:00:29');

