INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('1', '1', 'ge', 'აირიდეთ რიგები რეგისტრატურაში', '<p>გადაიხადეთ თანხა წინასწარ და რიგში დგომის გარეშე შედით ექიმთან.</p>', NULL, NULL, '2019-06-07 08:27:06', '2019-12-06 11:10:43');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('2', '1', 'en', 'AVOID QUEUES AT THE RECEPTION', '<p>Pay for the service in advance and enter the doctor\'s office without waiting in line.</p>', NULL, NULL, '2019-06-07 08:27:06', '2019-12-06 11:11:23');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('3', '2', 'ge', 'დაჯავშნეთ ექიმთან ვიზიტი', '<p>გამოიყენეთ ძებნის კრიტერიუმების ფართო არჩევანი, იპოვეთ ოჯახის ექიმი ან სპეციალისტი და დაჯავშნეთ მასთან ვიზიტი.</p>', NULL, NULL, '2019-06-07 08:27:34', '2019-12-09 11:52:42');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('4', '2', 'en', 'BOOK THE DOCTOR\'S APPOINTMENT', '<p>Use a wide selection of search filters, find general practitioners or specialists and book an appointment with them.</p>', NULL, NULL, '2019-06-07 08:27:34', '2019-12-06 11:16:00');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('5', '3', 'ge', 'დაჯავშნეთ სატელეფონო კონსულტაცია', '<p>მიიღეთ სატელეფონო კონსულტაცია ექიმისგან და დაზოგეთ დრო.</p>', NULL, NULL, '2019-06-07 08:27:54', '2019-12-06 11:12:04');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('6', '3', 'en', 'BOOK THE TELEPHONE CONSULTATION', '<p>Consult with the physician over the phone and save time.</p>', NULL, NULL, '2019-06-07 08:27:54', '2019-12-06 11:12:30');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('7', '4', 'ge', 'მიიღეთ ელექტრონული დანიშნულებები', '<p>ვიზიტის შემდეგ, დიაგნოზთან ერთად მიიღეთ დანიშნული მედიკამენტები, კვლევები, მიმართვები სპეციალისტთან და რეკომენდაციები.</p>', NULL, NULL, '2019-06-07 08:28:34', '2019-12-06 11:06:29');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('8', '4', 'en', 'RECEIVE ELECTRONIC PRESCRIPTIONS', '<p>Together with your diagnosis, obtain your electronic prescriptions for medications and imagings, referrals to specialists, and recommendations.</p>', NULL, NULL, '2019-06-07 08:28:34', '2019-12-06 11:07:17');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('9', '5', 'ge', 'მოძებნეთ მედიკამენტები', '<p>მოძებნეთ მედიკამენტები და მათი ფასები. ნახეთ, რომელ აფთიაქში შეგიძლიათ მათი შეძენა ყველაზე ახლოს და იაფად.</p>', NULL, NULL, '2019-06-07 08:28:56', '2019-12-06 11:07:53');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('10', '5', 'en', 'SEARCH FOR MEDICATIONS', '<p>Search for medications and their prices. Find the nearby pharmacies where you can buy them at the lowest price.</p>', NULL, NULL, '2019-06-07 08:28:56', '2019-12-06 11:08:33');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('15', '8', 'ge', 'შეამცირებთ რიგებს', '<p>მომხმარებლები პირდაპირ გადაიხდიან სერვისის ფულს და შესაბამისად ადამიანი ფულის გადახდის რიგში არ ჩადგება და პირდაპირ ექიმთან მივა. ამითი რიგი შემცირდება.</p>', NULL, NULL, '2019-06-21 05:03:17', '2019-07-05 12:39:59');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('16', '8', 'en', 'დაჯავშნეთ სატელეფონო კონსულტაცია', '<p>კვალიფიციური ექიმები თქვენს სამსახურში.</p>', NULL, NULL, '2019-06-21 05:03:17', '2019-06-21 05:03:17');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('49', '25', 'ge', 'გაზრდით პაციენტების ნაკადს', '<p>ექიმოში ბევრი ადამიანი დარეგისტრირდება, რადგან ის კლინიკებისა და ექიმების ძებნის პროცესს მოსახერხებელს ხდის. ეს EKIMO-ში ჩართულ კლინიკებში გაზრდის პაციენტების ნაკადს.</p>', NULL, NULL, '2019-07-01 12:43:56', '2019-12-06 12:33:52');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('50', '25', 'en', 'YOU WILL INCREASE THE PATIENT FLOW', '<p>Many people will register in EKIMO, as it makes the process of searching for clinics and doctors convenient. This will increase the patient flow in provider clinics.</p>', NULL, NULL, '2019-07-01 12:43:56', '2019-12-06 12:35:30');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('51', '26', 'ge', 'გაზრდით ცნობადობასა და რეპუტაციას', '<p>პაციენტები ექიმებს 5-ბალიანი სკალით შეაფასებენ. რეიტინგს მომხმარებლები დაჯავშნისას გამოიყენებენ. ექიმის კარგი რეიტინგი თქვენი კლინიკის პოპულარობაზე აისახება.</p>', NULL, NULL, '2019-07-01 12:44:38', '2019-12-06 12:52:05');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('52', '26', 'en', 'YOU\'LL BE MORE POPULAR AND REPUTABLE', '<p>Patients will evaluate the doctors by a 5-point scale. Users will use the rating when booking doctors. Your doctor\'s good rating will foster the popularity of your clinic.</p>', NULL, NULL, '2019-07-01 12:44:38', '2019-12-06 12:52:42');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('53', '27', 'ge', 'გამარტივებული გადახდა', '<p>შემთხვევითად გენერირებული ტექსტი ეხმარება დიზაინერებს და ტიპოგრაფიული ნაწარმის შემქმნელებს, რეალურთან მაქსიმალურად მიახლოებული შაბლონი წარუდგინონ შემფასებელს.</p>', NULL, NULL, '2019-07-01 12:45:40', '2019-07-01 16:16:45');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('54', '27', 'en', 'გამარტივებული გადახდა', NULL, NULL, NULL, '2019-07-01 12:45:40', '2019-07-01 12:45:40');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('55', '28', 'ge', 'სამედიცინო ტრეფიკის მონეტიზაცია', '<p>შემთხვევითად გენერირებული ტექსტი ეხმარება დიზაინერებს და ტიპოგრაფიული ნაწარმის შემქმნელებს, რეალურთან მაქსიმალურად</p>', NULL, NULL, '2019-07-01 12:46:12', '2019-07-01 16:16:42');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('56', '28', 'en', 'სამედიცინო ტრეფიკის მონეტიზაცია', '<p>შემთხვევითად გენერირებული ტექსტი ეხმარება დიზაინერებს და ტიპოგრაფიული ნაწარმის შემქმნელებს, რეალურთან მაქსიმალურად მიახლოებული შაბლონი წარუდგინონ შემფასებელს.</p>', NULL, NULL, '2019-07-01 12:46:12', '2019-07-01 12:46:12');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('57', '29', 'ge', 'კმაყოფილი პაციენტები', '<p>შემთხვევითად გენერირებული ტექსტი ეხმარება დიზაინერებს და ტიპოგრაფიული ნაწარმის შემქმნელებს, რეალურთან მაქსიმალურად</p>', NULL, NULL, '2019-07-01 12:46:29', '2019-07-01 16:16:49');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('58', '29', 'en', 'პაციენტების ლოიალობა', NULL, '<p>შემთხვევითად გენერირებული ტექსტი ეხმარება დიზაინერებს და ტიპოგრაფიული ნაწარმის შემქმნელებს, რეალურთან მაქსიმალურად მიახლოებული შაბლონი წარუდგინონ შემფასებელს.</p>', NULL, '2019-07-01 12:46:29', '2019-07-01 12:46:29');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('61', '31', 'ge', 'მიიღეთ და გამოიყენეთ საგარანტიო წერილი', '<p>ოჯახის ექიმთან სატელეფონო კონსულტაციით, მიიღეთ საგარანტიო წერილი და გამოიყენეთ ის აპლიკაციით სპეციალისტის დაჯავშნისას.</p>', NULL, NULL, '2019-07-01 14:03:25', '2019-12-06 11:03:59');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('62', '31', 'en', 'RECEIVE AND USE THE LETTER OF GUARANTEE', '<p>Consult with your family doctor over the phone and receive the letter of guarantee when booking an appointment with the specialist.</p>', NULL, NULL, '2019-07-01 14:03:25', '2019-12-06 11:04:53');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('63', '32', 'ge', 'მოაწესრიგებთ ჯავშნებს და შეამცირებთ რიგებს', '<div class=\"col-lg-6 col-xl-4 benefits mb-30\">\r\n<div class=\"services-section_container d-flex\">\r\n<div class=\"d-flex flex-column \">\r\n<div class=\"services-section_item-descr\">ვიზიტის დაჯავშნა და საფასურის გადახდა აპლიკაციაშივეა შესაძლებელი, რაც ნიშნავს, რომ პაციენტებს რიგში დგომა აღარ მოუწევთ. შესაბამსად, კლინიკები უფრო ორგანიზებულად მიიღებენ პაციენტებს.</div>\r\n</div>\r\n</div>\r\n</div>', NULL, NULL, '2019-07-01 16:17:22', '2019-12-06 12:47:08');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('64', '32', 'en', 'YOU WILL IMPROVE YOUR BOOKING SYSTEM AND REDUCE QUEUES', '<div class=\"col-lg-6 col-xl-4 benefits mb-30\">\r\n<div class=\"services-section_container d-flex\">\r\n<div class=\"d-flex flex-column \">\r\n<div class=\"services-section_item-descr\">Patients will book and pay for services in the app, which means that they will no longer have to stand in line. In this way, clinics will serve the patients in a more organized way.</div>\r\n</div>\r\n</div>\r\n</div>', NULL, NULL, '2019-07-01 16:17:22', '2019-12-06 12:47:50');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('65', '33', 'ge', 'გააუმჯობესებთ პაციენტების ინფორმირებულობასა და მკურნალობაზე დამყოლობას', '<p>პაციენტი აპლიკაციაში იღებს ელექტრონულ დიაგნოზებს, რეკომენდაციებს, დანიშნულ მედიკამენტებს, კვლევებსა და მიმართვებს, რაც აუმჯობესებს პაციენტების ინფორმირებულობასა და მკურნალობაზე დამყოლობას.</p>', NULL, NULL, '2019-07-01 16:17:55', '2019-12-06 12:45:17');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('66', '33', 'en', 'YOU\'LL IMPROVE THE PATIENT AWARENESS AND COMPLIANCE', '<p>The patients receive electronic diagnoses, recommendations, prescribed medications, referrals to specialists and services in the app, which improves patient awareness and compliance to treatment.</p>', NULL, NULL, '2019-07-01 16:17:55', '2019-12-06 12:46:21');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('67', '34', 'ge', 'პაციენტებს დისტანციურ მომსახურებას შესთავაზებთ', '<p style=\"text-align: justify;\">ექიმებს საშუალება ექნებათ, პაციენტებს სრულფასოვანი და ფასიანი სატელეფონო კონსულტაცია გაუწიონ, მაგალითად, ის სპეციალისტთან გადაამისამართონ ან მედიკამენტი დაუნიშნონ.</p>', NULL, NULL, '2019-07-01 16:18:54', '2019-12-06 12:49:24');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('68', '34', 'en', 'YOU WILL OFFER REMOTE MEDICAL SERVICES TO PATIENTS', '<p>Doctors will be able to provide comprehensive, paid telephone consultation. For example, they will refer a patient to a specialist remotely or prescribe a medication.</p>', NULL, NULL, '2019-07-01 16:18:54', '2019-12-06 12:50:32');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('69', '35', 'ge', 'პაციენტებს დისტანციურ მომსახურებას შესთავაზებთ', '<p>საშუალება გექნებათ, პაციენტებს სრულფასოვანი და ფასიანი სატელეფონო კონსულტაცია გაუწიოთ, მაგალითად, მედიკამენტი დაუნიშნოთ ან რეკომენდაცია მისცეთ.</p>', NULL, NULL, '2019-07-01 16:23:09', '2019-12-06 12:42:40');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('70', '35', 'en', 'YOU WILL OFFER REMOTE MEDICAL SERVICES TO PATIENTS', '<div class=\"col-lg-6 col-xl-4 benefits mb-30\">\r\n<div class=\"services-section_container d-flex\">\r\n<div class=\"d-flex flex-column \">\r\n<div class=\"services-section_item-descr\">You will be able to provide comprehensive, paid telephone consultation. For example, you will prescribe a medication or give them a recommendation. </div>\r\n</div>\r\n</div>\r\n</div>', NULL, NULL, '2019-07-01 16:23:09', '2019-12-06 12:43:22');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('71', '36', 'ge', 'გექნებათ წვდომა მეტ პაციენტზე', '<p>პაციენტების უმრავლესობა ექიმს რეკომენდაციის საფუძველზე აკითხავს. EKIMO-ში ჩაშენებული სარეიტინგო სისტემა პაციენტების ახალ ნაკადს გაგიჩენთ.</p>', NULL, NULL, '2019-07-01 16:23:35', '2019-12-06 12:36:25');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('72', '36', 'en', 'YOU\'LL BE ABLE TO REACH MORE PATIENTS', '<p>Most patients refer to doctor based on recommendations. In-built rating system will help you to reach out for a whole new patient population, based on your rating score.</p>', NULL, NULL, '2019-07-01 16:23:35', '2019-12-06 12:37:35');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('73', '37', 'ge', 'გამოიყენებთ სამედიცინო საინფორმაციო სისტემას უფასოდ', '<p>ექიმები ტრადიციული ქაღალდმატარებლების ნაცვლად, გამოიყენებენ სისტემას, რომელშიც ელექტრონულად შეავსებენ დანიშნულებებს, დიაგნოზებს, რეცეპტებს (ინტეგრირებულია ჯანდაცვის სამინისტროს პორტალთან) და გაუგზავნიან მათ პაციენტებს.</p>', NULL, NULL, '2019-07-01 16:24:17', '2019-12-06 12:31:04');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('74', '37', 'en', 'YOU\'LL USE THE MEDICAL INFORMATION SYSTEM FOR FREE', '<p>Doctors will substitute traditional paper-based medical documentation with electronic medical records, where they can specify diagnosis and generate electronic prescriptions (integrated with MOH services) and exchange them with patients via app.</p>', NULL, NULL, '2019-07-01 16:24:17', '2019-12-06 12:32:00');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('75', '38', 'ge', 'გაზრდით შემოსავალს', '<div class=\"container-fluid\">\r\n<div class=\"row services-section_items service-section-providers_items\">\r\n<div class=\"col-lg-6 col-xl-4 benefits mb-30\">\r\n<div class=\"services-section_container d-flex\">\r\n<div class=\"d-flex flex-column \">\r\n<div class=\"services-section_item-descr\">EKIMO-ს გამოყენებით, გეყოლებათ მეტი პაციენტი. თუ კარგი რეიტინგი გექნებათ, გეყოლებათ მეტი პაციენტი და ფასის მომატებასაც შეძლებთ. შედეგად, გაგეზრდებათ შემოსავალი.</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<footer class=\"site__footer\"></footer>', NULL, NULL, '2019-07-01 16:24:39', '2019-12-06 13:29:56');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('76', '38', 'en', 'YOU\'LL HAVE A BETTER INCOME', '<div class=\"container-fluid\">\r\n<div class=\"row services-section_items service-section-providers_items\">\r\n<div class=\"col-lg-6 col-xl-4 benefits mb-30\">\r\n<div class=\"services-section_container d-flex\">\r\n<div class=\"d-flex flex-column \">\r\n<div class=\"services-section_item-descr\">EKIMO will bring you more patients. If you have a good rating, more patiens will choose to visit you, which will allow you to increase the price. Thus, your income will increase.</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', NULL, NULL, '2019-07-01 16:24:39', '2019-12-06 13:30:51');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('77', '39', 'ge', 'მოაწესრიგებთ ჩაწერებს', '<p>ელექტრონული ჯავშნები ჩაწერის პროცესის მოწესრიგების საშუალებას მოგცემთ. თქვენთან აღარ იარსებებს შეცდომები, რომლებიც ხშირია ფურცლით მუშაობის დროს.</p>', NULL, NULL, '2019-07-01 16:25:04', '2019-12-06 12:39:47');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('78', '39', 'en', 'YOU BOOKING SYSTEM WILL IMPROVE', '<p>Electronic booking allows you to streamline the process. You will no longer have errors that are so common when working on paper.</p>', NULL, NULL, '2019-07-01 16:25:04', '2019-12-06 12:40:18');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('79', '40', 'ge', 'გაზრდით ცნობადობასა და რეპუტაციას', '<p>პაციენტები ექიმებს 5-ბალიანი სკალით შეაფასებენ. რეიტინგს მომხმარებლები ექიმების დაჯავშნისას გამოიყენებენ. კარგი რეიტინგი თქვენს რეპუტაციასა და პოპულარობაზე აისახება.</p>', NULL, NULL, '2019-07-01 16:25:24', '2019-12-11 07:48:22');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('80', '40', 'en', 'YOU\'LL BE MORE POPULAR AND REPUTABLE', '<p>Patients will evaluate the doctors by a 5-point scale. Users will use the rating when booking doctors. Better rating will foster your reputation and popularity.</p>', NULL, NULL, '2019-07-01 16:25:24', '2019-12-06 12:30:26');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('89', '45', 'ge', 'გამოიყენებთ სამედიცინო საინფორმაციო სისტემას უფასოდ', '<div class=\"container-fluid\">\r\n<div class=\"row services-section_items service-section-providers_items\">\r\n<div class=\"col-lg-6 col-xl-4 benefits mb-30\">\r\n<div class=\"services-section_container d-flex\">\r\n<div class=\"d-flex flex-column \">\r\n<div class=\"services-section_item-descr\">ექიმები ტრადიციული ქაღალდმატარებლების ნაცვლად, გამოიყენებენ სისტემას, რომელშიც ელექტრონულად შეავსებენ დანიშნულებებს, დიაგნოზებს, რეცეპტებს (ინტეგრირებულია ჯანდაცვის სამინისტროს პორტალთან) და გაუგზავნიან მათ პაციენტებს.</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', NULL, NULL, '2019-07-10 14:29:11', '2019-12-06 12:40:54');
INSERT INTO `catalog_languages` (`id`, `catalog_id`, `language`, `title`, `description`, `content`, `meta_desc`, `created_at`, `updated_at`) VALUES ('90', '45', 'en', 'YOU\'LL USE THE MEDICAL INFORMATION SYSTEM FOR FREE', '<p>Doctors will substitute traditional paper-based medical documentation with electronic medical records, where they can specify diagnosis and generate electronic prescriptions (integrated with MOH services) and exchange them with patients via app.</p>', NULL, NULL, '2019-07-10 14:29:11', '2019-12-06 12:41:49');

