INSERT INTO `faq_languages` (`id`, `faq_id`, `language`, `title`, `description`, `created_at`, `updated_at`) VALUES ('49', '25', 'ge', 'შემიძლია მედიკამენტი დანიშნულებიდანვე მოვძებნო?', '<p><span data-sheets-value=\"{\" data-sheets-userformat=\"{\">კონკრეტული დანიშნულების გვერდზე თქვენ შეგიძლიათ იხილოთ ღილაკი \"ყველა მედიკამენტის დამატება პაკეტში\". ამ ღილაკზე დაჭერით, დანიშნულებაში მითითებული ყველა მედიკამენტი ავტომატურად დაემატება მედიკამენტების პაკეტში და თქვენ აღარ მოგიწევთ მათი ცალ-ცალკე დამატება მედიკამენტების ძებნის მოდულში.<br /><br />თუ გსურთ, რომ დანიშნულების მოდულიდან მხოლოდ კონკრეტული მედიკამენტი დაამატოთ პაკეტში, საჭიროა დანიშნულების მოდულში გადახვიდეთ ტაბზე \"მედიკამენტები\", აქ კონკრეტული მედიკამენტის გვერდზე შესვლისას დაინახავთ ღილაკს \"მედიკამენტის დამატება პაკეტში\". ამ ღილაკზე დაჭერით მედიკამენტი პაკეტში დაემატება.</span></p>', '2019-06-26 09:34:39', '2019-07-04 07:47:07');
INSERT INTO `faq_languages` (`id`, `faq_id`, `language`, `title`, `description`, `created_at`, `updated_at`) VALUES ('50', '25', 'en', 'შემიძლია მედიკამენტი დანიშნულებიდანვე მოვძებნო?', '<p><span data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;კონკრეტული დანიშნულების გვერდზე თქვენ შეგიძლიათ იხილოთ ღილაკი \\&quot;ყველა მედიკამენტის დამატება პაკეტში\\&quot;. ამ ღილაკზე დაჭერით, დანიშნულებაში მითითებული ყველა მედიკამენტი ავტომატურად დაემატება მედიკამენტების პაკეტში და თქვენ აღარ მოგიწევთ მათი ცალ-ცალკე დამატება მედიკამენტების ძებნის მოდულში.\\n\\nთუ გსურთ, რომ დანიშნულების მოდულიდან მხოლოდ კონკრეტული მედიკამენტი დაამატოთ პაკეტში, საჭიროა დანიშნულების მოდულში გადახვიდეთ ტაბზე \\&quot;მედიკამენტები\\&quot;, აქ კონკრეტული მედიკამენტის გვერდზე შესვლისას დაინახავთ ღილაკს \\&quot;მედიკამენტის დამატება პაკეტში\\&quot;. ამ ღილაკზე დაჭერით მედიკამენტი პაკეტში დაემატება.&quot;}\" data-sheets-userformat=\"{&quot;2&quot;:5033,&quot;3&quot;:{&quot;1&quot;:0},&quot;6&quot;:{&quot;1&quot;:[{&quot;1&quot;:2,&quot;2&quot;:0,&quot;5&quot;:[null,2,0]},{&quot;1&quot;:0,&quot;2&quot;:0,&quot;3&quot;:3},{&quot;1&quot;:1,&quot;2&quot;:0,&quot;4&quot;:2}]},&quot;8&quot;:{&quot;1&quot;:[{&quot;1&quot;:2,&quot;2&quot;:0,&quot;5&quot;:[null,2,0]},{&quot;1&quot;:0,&quot;2&quot;:0,&quot;3&quot;:3},{&quot;1&quot;:1,&quot;2&quot;:0,&quot;4&quot;:1}]},&quot;10&quot;:1,&quot;11&quot;:4,&quot;12&quot;:0,&quot;15&quot;:&quot;arial,sans,sans-serif&quot;}\">კონკრეტული დანიშნულების გვერდზე თქვენ შეგიძლიათ იხილოთ ღილაკი \"ყველა მედიკამენტის დამატება პაკეტში\". ამ ღილაკზე დაჭერით, დანიშნულებაში მითითებული ყველა მედიკამენტი ავტომატურად დაემატება მედიკამენტების პაკეტში და თქვენ აღარ მოგიწევთ მათი ცალ-ცალკე დამატება მედიკამენტების ძებნის მოდულში.<br /><br />თუ გსურთ, რომ დანიშნულების მოდულიდან მხოლოდ კონკრეტული მედიკამენტი დაამატოთ პაკეტში, საჭიროა დანიშნულების მოდულში გადახვიდეთ ტაბზე \"მედიკამენტები\", აქ კონკრეტული მედიკამენტის გვერდზე შესვლისას დაინახავთ ღილაკს \"მედიკამენტის დამატება პაკეტში\". ამ