INSERT INTO `faq_languages` (`id`, `faq_id`, `language`, `title`, `description`, `created_at`, `updated_at`) VALUES ('51', '26', 'ge', 'რა სახის ელექტრონულ დოკუმენტებს მივიღებ?', '<p><span data-sheets-value=\"{\" data-sheets-userformat=\"{\">სხვადასხვა ტიპის სერვისის მომწოდებლებისგან მიიღებთ განსხვავებულ ელექტრონულ დოკუმენტებს, კერძოდ:&nbsp;<br /><br />&bull; სამედიცინო დაწესებულებებისგან მიიღებთ დანიშნულებებს, სადაც გაერთიანებული იქნება დიაგნოზები, რეკომენდაციები, მიმართვები სამედიცინო სერვისებსა და მედიკამენტებზე.&nbsp;<br /><br />&bull; სადაზღვევო კომპანიებისგან ასევე მიიღებთ დანიშნულებებს და დამატებით საგარანტიო წერილებსაც, სადაც მითითებულია თანაგადახდის პროცენტი, რომელსაც სადაზღვევო კომპანია დაგიფინანსებთ. აღნიშნული საგარანტიო წერილების გამოყენება შეგიძლიათ ჯავშნის საფასურის გადახდისას, რათა დაფაროთ მხოლოდ ის თანხა, რომელსაც სადაზღვევო არ გიფინანსებთ.</span></p>', '2019-06-26 09:34:54', '2019-07-11 13:52:33');
