INSERT INTO `faq_languages` (`id`, `faq_id`, `language`, `title`, `description`, `created_at`, `updated_at`) VALUES ('53', '27', 'ge', 'როგორ მივიღებ ელექტრონულ დოკუმენტებს?', '<p><span data-sheets-value=\"{\" data-sheets-userformat=\"{\">EKIMO-ში ელექტრონული დოკუმენტების მიღება ორი გზითაა შესაძლებელი:&nbsp;<br /><br />&bull; თუ თქვენ EKIMO-დან შექმნით ჯავშანს, რომლის შედეგადაც ექიმი დანიშნულებას გამოგიწერთ, ამ დანიშნულებას პირდაპირ EKIMO-ში მიიღებთ.&nbsp;<br /><br />&bull; ელექტრონული დოკუმენტების მიღება შეგიძლიათ იმ სერვისის მომწოდებლებისგან, რომლებთანაც EKIMO-თი ხართ დაკავშირებული. დაკავშირებული სერვისის მომწოდებლების ნახვა/ცვლილება შეგიძლიათ საკუთარი პროფილიდან. სერვისის მომწოდებელთან დაკავშირების შემდეგ, თქვენ EKIMO-ში ავტომატურად მიიღებთ იმ ელექტრონულ დოკუმენტებს, რომლებსაც EKIMO-ს სერვისის მომწოდებელი ექიმები გამოგიწერენ, მათთან ვიზიტი EKIMO-ს გარეშე რომც განახორციელოთ.</span></p>', '2019-06-26 09:35:12', '2019-07-11 13:50:41');
