INSERT INTO `page_languages` (`id`, `page_id`, `language`, `title`, `short_title`, `description`, `content`, `meta_desc`, `proDescription`, `proTitle`, `proTitle1`, `proTitle2`, `formTitle`, `created_at`, `updated_at`) VALUES ('11', '6', 'ge', 'გაუქმების პირობები', 'გაუქმების პირობები', NULL, '<ol>\r\n<li>In case of cancellation by User:</li>\r\n<ol style=\"list-style-type: lower-alpha;\">\r\n<li>Users cancel their bookings anytime up to the time when the actual appointment should take a place.</li>\r\n<li>If the cancellation is due to the abusive nature of the User, such User shall not be eligible for any refund and EKIMO/Practitioner shall be entitled to take any legal action, depending upon the gravity of the matter.</li>\r\n<li>In case a consultation is cancelled by the User amounts paid by the Users with respect to the cancelled consultation shall be refunded in following manner:</li>\r\n<ol style=\"list-style-type: lower-roman;\">\r\n<li>If cancellation takes place before the day of the appointment, after 18:00 (Georgia time, GMT+4), user shall be fined with 100% of the amount that he/she paid via EKIMO for the appointment.</li>\r\n<li>If cancellation takes place between 4 days before the day of the appointment (not including the day of the appointment) up until the day before the appointment, until 18:00 (Georgia time, GMT+4), user shall be fined with 40% of the amount that he/she paid via EKIMO for the appointment.</li>\r\n<li>If cancellation takes place before 4 days before the day of the appointment (not including the day of the appointment) user shall not be fined and will receive the complete amount that he/she paid for the booking as a refund.</li>\r\n</ol>\r\n</ol>\r\n<li>In case of cancellation by Practitioner/Provider:</li>\r\n<ol style=\"list-style-type: lower-alpha;\">\r\n<li>EKIMO does not take any responsibility in case of cancellation of the appointment by Practitioners or Providers or if User considers consultation as inadequate. Settlement in this case is between the User and Practitioner or Provider.</li>\r\n</ol>\r\n</ol>', NULL, NULL, NULL, NULL, NULL, NULL, '2019-06-19 05:08:29', '2019-06-28 13:48:47');
