INSERT INTO `page_languages` (`id`, `page_id`, `language`, `title`, `short_title`, `description`, `content`, `meta_desc`, `proDescription`, `proTitle`, `proTitle1`, `proTitle2`, `formTitle`, `created_at`, `updated_at`) VALUES ('13', '7', 'ge', 'კლინიკებისთვის', 'კლინიკებისთვის', '<p>დაარეგისტრირეთ თქვენი კლინიკა აპლიკაცია EKIMO-ში და გახადეთ თქვენი სამედიცინო სერვისები პაციენტებისთვის უფრო კომფორტული და ხელმისაწვდომი. EKIMO დაგეხმარებათ მოაწესრიგოთ ჯავშნები და შეამციროთ რიგები, პაციენტებს დისტანციური მომსახურება შესთავაზოთ და გამოიყენოთ სამედიცინო საინფორმაციო სისტემა უფასოდ, EKIMO-ს აპლიკაციასთან ერთად. შედეგად, თქვენი პაციენტები უკეთეს სერვისს მიიღებენ, კლინიკა კი &ndash; მეტ შემოსავალს.</p>', '<p>რას მიიღებთ ექიმოში ჩართვით</p>', NULL, '<p>აპლიკაციაში ჩართულია</p>', 'ექიმი', 'კლინიკა', 'აფთიაქი', 'კლინიკის რეგისტრაცია', '2019-06-19 08:06:19', '2019-12-06 09:42:16');
INSERT INTO `page_languages` (`id`, `page_id`, `language`, `title`, `short_title`, `description`, `content`, `meta_desc`, `proDescription`, `proTitle`, `proTitle1`, `proTitle2`, `formTitle`, `created_at`, `updated_at`) VALUES ('14', '7', 'en', 'FOR CLINICS', 'FOR CLINICS', '<p>Register your clinic at EKIMO app and make your medical services more convenient and accessible to patients. With EKIMO you can manage appointments and reduce queues, offer remote services to patients, and use medical information system for free, together with EKIMO app. As a result, the patients will get a better service, and your clinic will increase the revenue.</p>', '<p>Benefits of registering in EKIMO</p>', NULL, '<p>THE APP COMBINES</p>', 'DOCTORS', 'CLINICS', 'PHARMACIES', 'CLINIC REGISTRATION', '2019-06-19 08:06:19', '2019-12-06 10:53:08');
